        <span class="token punctuation">}</span>
        <span class="token function">backtracking</span><span class="token punctuation">(</span>list<span class="token punctuation">,</span><span class="token string">""</span><span class="token punctuation">,</span>digits<span class="token punctuation">,</span><span class="token number">0</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
