            res<span class="token punctuation">.</span><span class="token function">add</span><span class="token punctuation">(</span><span class="token function">path2String</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
