        <span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token keyword">char</span> ss <span class="token operator">:</span> sc<span class="token punctuation">)</span> <span class="token punctuation">{</span>
            array<span class="token punctuation">[</span>ss <span class="token operator">-</span> <span class="token char">'a'</span><span class="token punctuation">]</span><span class="token operator">++</span><span class="token punctuation">;</span>
