        <span class="token keyword">char</span><span class="token punctuation">[</span><span class="token punctuation">]</span> tc <span class="token operator">=</span> magazine<span class="token punctuation">.</span><span class="token function">toCharArray</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
